:root {
  --font-stack: 'Latin Modern Roman', Cambria, Cochin, Georgia, Times, 'Times New Roman', 'Noto Serif CJK SC', 'Source Han Serif CN', Simsun, serif ;
  --base64-acrylic: url('data:image/png;base64,');
}

html, body {
  font-family: var(--font-stack);
}

@keyframes change-color {
  from {
    filter: hue-rotate(0deg);
  }
  to {
    filter: hue-rotate(360deg);
  }
}

.Colour {
  background-image:
    linear-gradient(217deg, rgba(255,0,0,.8), rgba(255,0,0,0) 70.71%),
    linear-gradient(127deg, rgba(0,255,0,.8), rgba(0,255,0,0) 70.71%),
    linear-gradient(336deg, rgba(0,0,255,.8), rgba(0,0,255,0) 70.71%);
    animation: change-color 16s infinite linear;
}

.Settings .plane {
  background-image: var(--base64-acrylic);
  background-color: rgba(255, 255, 255, 0.3);
  scrollbar-width: thin;
  scrollbar-color: rgba(255, 255, 255, 0.7) rgba(255, 255, 255, 0.3);
}

.Settings .plane .Widget, .Settings .plane select {
  background-color: rgba(255, 255, 255, 0.3) !important;
  box-shadow: 3px 3px 5px rgba(0, 0, 0, 0.1);
  border: none;
}
